export function getActiveFilePath() {
  return process.env.ACTIVE_FILE_PATH || '__active_file__';
}

function load() {
  try {
    const path = process.env.ACTIVE_FILE_PATH || '__active_file__';
    if (!path) {return undefined;};
    return import(path);
  } catch (_) {
    return undefined;
  }
};

export default load;
